import React, { useEffect, useRef, useState } from "react";
import "../css/tools.css";
import Select from 'react-select';
import {
  FontStyles,
  Fonts,
  customStyles,
  alignStyles,
  AlignPlaceholder,
  FontSizes,
  formatOptionLabel,
  Align,
  Colors,
  ColorsPlacehoder
} from "../constants/dropdowns";
import "../css/editor.css";

export default function Editor() {
  const editorRef = useRef(null);
  const [currentBlockId, setCurrentBlockId] = useState(1);
  const [blocks, setBlocks] = useState([{ id: 1, content: '' }]);
  const [currentFontFamily, setFontFamily] = useState('Arial');
  const [currentFontSize, setFontSize] = useState('11px');
  const options = ["File", "Edit", "View", "Insert", "Format", "Tools", "Extensions", "Help"];

  const handleKeyDown = (event, blockId) => {
    setCurrentBlockId(blockId);

    if (event.key === 'Enter') {
      event.preventDefault();

      const newBlockId = blockId + 1;

      const blockIndex = blocks.findIndex((block) => block.id === blockId);
      const updatedBlocks = [
        ...blocks.slice(0, blockIndex + 1),
        { id: newBlockId, content: '' },
        ...blocks.slice(blockIndex + 1),
      ];
      setBlocks(updatedBlocks);

      setTimeout(() => {
        const newBlockElement = document.getElementById(`block-${newBlockId}`);
        if (newBlockElement) {
          newBlockElement.style.fontSize = currentFontSize;
          newBlockElement.style.fontFamily = currentFontFamily;
          newBlockElement.focus();
        }
      }, 0);
    } else if (event.key === "ArrowUp" && blockId > 1) {
      const beforeElement = document.getElementById(`block-${blockId - 1}`);
      if (beforeElement) {
        beforeElement.focus();
      }
    } else if (event.key === "ArrowDown" && blockId < blocks.length) {
      const afterElement = document.getElementById(`block-${blockId + 1}`);
      if (afterElement) {
        afterElement.focus();
      }
    }
  };

  const handleChange = (event, blockId) => {
    setCurrentBlockId(blockId);
    const blockIndex = blocks.findIndex((block) => block.id === blockId);
    const updatedBlocks = [...blocks];
    updatedBlocks[blockIndex].content = event.target.innerHTML;
    setBlocks(updatedBlocks);
  };

  const handleText = (text) => {
    const type = text.value;
    const currentBlockElement = document.getElementById(`block-${currentBlockId}`);
    if (currentBlockElement) {
      currentBlockElement.style.fontFamily = "Roboto";

      if (type === "normal") {
        currentBlockElement.style.fontSize = "25px";
      } else if (type === "title") {
        currentBlockElement.style.fontSize = "48px";
      } else if (type === "heading1") {
        currentBlockElement.style.fontSize = "40px";
        currentBlockElement.style.fontWeight = "bold";
      } else if (type === "heading2") {
        currentBlockElement.style.fontSize = "30px";
        currentBlockElement.style.fontWeight = "bold";
      } else {
        currentBlockElement.style.fontSize = "25px";
        currentBlockElement.style.fontWeight = "bold";
      }

      currentBlockElement.focus();
    }
  };

  const handleFontBold = () => {
	const currentBlockElement = document.getElementById(`block-${currentBlockId}`);
	if (currentBlockElement) {
	  const fontWeight = currentBlockElement.style.fontWeight;
	  currentBlockElement.style.fontWeight = fontWeight === "bold" ? "400" : "bold";
	  currentBlockElement.focus();
	}
  };

  const handleFontItalic = () => {
	const currentBlockElement = document.getElementById(`block-${currentBlockId}`);
	if (currentBlockElement) {
	  const fontStyle = currentBlockElement.style.fontStyle;
	  currentBlockElement.style.fontStyle = fontStyle === "italic" ? "normal" : "italic";
	  currentBlockElement.focus();
	}
  };

  const handleFontUnderline = () => {
	const currentBlockElement = document.getElementById(`block-${currentBlockId}`);
	if (currentBlockElement) {
	  const textDecoration = currentBlockElement.style.textDecoration;
	  currentBlockElement.style.textDecoration = textDecoration === "underline" ? "none" : "underline";
	  currentBlockElement.focus();
	}
  };

  const handleTextStrikeThrough = () => {
	const currentBlockElement = document.getElementById(`block-${currentBlockId}`);
	if (currentBlockElement) {
	  const textDecoration = currentBlockElement.style.textDecoration;
	  currentBlockElement.style.textDecoration = textDecoration.includes("line-through") ? "none" : "line-through";
	  currentBlockElement.focus();
	}
  };

  const handleHighlight = () => {
	const currentBlockElement = document.getElementById(`block-${currentBlockId}`);
	if (currentBlockElement) {
	  const backgroundColor = currentBlockElement.style.backgroundColor;
	  currentBlockElement.style.backgroundColor = backgroundColor === "yellow" ? "transparent" : "yellow";
	  currentBlockElement.focus();
	}
  };

  const AlignContent = (align_val) => {
	const currentBlockElement = document.getElementById(`block-${currentBlockId}`);
	if (currentBlockElement) {
	  currentBlockElement.style.textAlign = align_val.value;
	  currentBlockElement.focus();
	}
  };

  const ChangeFontSize = (font_size_val) => {
	setFontSize(`${font_size_val.value}px`);
	const currentBlockElement = document.getElementById(`block-${currentBlockId}`);
	if (currentBlockElement) {
	  currentBlockElement.style.fontSize = `${font_size_val.value}px`;
	  currentBlockElement.focus();
	}
  };

  const ChangeFontFamily = (fontfamily) => {
	setFontFamily(fontfamily.value);
	const currentBlockElement = document.getElementById(`block-${currentBlockId}`);
	if (currentBlockElement) {
	  currentBlockElement.style.fontFamily = fontfamily.value;
	  currentBlockElement.focus();
	}
  };

  const handleColorChange = (gradients) => {
	const currentBlockElement = document.getElementById(`block-${currentBlockId}`);
	if (currentBlockElement) {
	  currentBlockElement.style.color = gradients.value;
	  currentBlockElement.focus();
	}
  };


  const handleTextColorChange = (color) => {
	const currentBlockElement = document.getElementById(`block-${currentBlockId}`);
	if (currentBlockElement) {
	  currentBlockElement.style.color = color;
	  currentBlockElement.focus();
	}
  };

  return (
	<div className="editor-container">
	  <div className="toolbar">
		<div className="formatting-tools">
		  <div className="dropdowns">
			<Select
			  className="text-dropdown"
			  options={FontStyles}
			  styles={customStyles}
			  placeholder="Text"
			  formatOptionLabel={formatOptionLabel}
			  onChange={handleText}
			/>
			<Select
			  className="text-dropdown"
			  options={Fonts}
			  styles={customStyles}
			  placeholder="Font"
			  formatOptionLabel={formatOptionLabel}
			  onChange={ChangeFontFamily}
			/>
			<Select
			  className="text-dropdown"
			  options={FontSizes}
			  styles={customStyles}
			  placeholder="Font Size"
			  formatOptionLabel={formatOptionLabel}
			  onChange={ChangeFontSize}
			/>
		  </div>
		  <div className="text-tools">
			<button className="icon-button" onClick={handleFontBold}>
			  <i className="fas fa-bold"></i>
			</button>
			<button className="icon-button" onClick={handleFontItalic}>
			  <i className="fas fa-italic"></i>
			</button>
			<button className="icon-button" onClick={handleFontUnderline}>
			  <i className="fas fa-underline"></i>
			</button>
			<button className="icon-button" onClick={handleTextStrikeThrough}>
			  <i className="fas fa-strikethrough"></i>
			</button>
			<button className="icon-button" onClick={handleHighlight}>
			  <i className="fas fa-highlighter"></i>
			</button>
			<Select
			  className="text-dropdown"
			  options={Align}
			  styles={alignStyles}
			  placeholder={<AlignPlaceholder />}
			  formatOptionLabel={formatOptionLabel}
			  onChange={AlignContent}
			/>
			<Select
			  className="color-dropdown"
			  options={Colors}
			  styles={customStyles}
			  placeholder={<ColorsPlacehoder />}
			  formatOptionLabel={formatOptionLabel}
			  onChange={handleColorChange}
			/>
			<input
			  type="color"
			  className="color-picker"
			  onChange={(e) => handleTextColorChange(e.target.value)}
			/>
		  </div>
		</div>
	  </div>
	  <div
		ref={editorRef}
		className="editor"
		contentEditable="true"
		suppressContentEditableWarning={true}
	  >
		{blocks.map((block) => (
		  <div
			key={block.id}
			id={`block-${block.id}`}
			className="block"
			contentEditable="true"
			onKeyDown={(e) => handleKeyDown(e, block.id)}
			onInput={(e) => handleChange(e, block.id)}
		  >
			{block.content}
		  </div>
		))}
	  </div>
	</div>
  );
		};
